import './TodoSearch.css'
import React from 'react';

function TodoSearch(){
    const [searchValue, setsearchValue] = React.useState('');



return(

    <input placeholder="Buscar Todos"
    value={searchValue}
    className="TodoSearch" onChange={(event)=> {
        setsearchValue(event.target.value);
        
    }}></input>
);

}

export {TodoSearch};
